public class ArrTestFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int [] a = {1,2,3,4,5};
		int temp = 0;
		
		for(int five : a) {
			
			
			
			temp += five;
			System.out.println(five);
			
			
			
		}
		System.out.println(temp);
	}

}
